# inicializador de micro-serviços. Pre-requisitos: docker rodando e conexão a internet.$waitTime = 2$containerName = 'control-cache'$containerStatus = docker inspect --format="{{.State.Status}}" $containerName 2>&1# docker conteiner para o redis serverif ($containerStatus -eq 'running') {    Write-Output "Container $containerName is already running"} elseif ($containerStatus -eq 'exited') {    docker start $containerName | Out-Null    Write-Output "Container $containerName has been started"    Start-Sleep -Seconds 3} elseif ($containerStatus -eq 'paused') {    docker unpause $containerName | Out-Null    Write-Output "Container $containerName has been unpaused"    Start-Sleep -Seconds 3} else {    docker run --name $containerName -p 6379:6379 -d redis | Out-Null    Write-Output "Container $containerName has been created and started"    Start-Sleep -Seconds 10}# catalogo de micro-serviços$job1 = Start-Job -ScriptBlock { Set-Location 'C:\Users\gabri\OneDrive\Área de Trabalho\frank\projeto final\backend\service finder'; .\venv\Scripts\Activate.ps1; "Starting Service Finder"; waitress-serve --listen=127.0.0.1:8001 main:app }Start-Sleep -Seconds $waitTime# micro-serviço de registrosStart-Sleep -Seconds $waitTime$job2 = Start-Job -ScriptBlock { Set-Location 'C:\Users\gabri\OneDrive\Área de Trabalho\frank\projeto final\backend\logging service'; .\venv\Scripts\Activate.ps1; uvicorn main:app --host 0.0.0.0 --port 8002 }# micro-serviço de parametros$job3 = Start-Job -ScriptBlock { Set-Location 'C:\Users\gabri\OneDrive\Área de Trabalho\frank\projeto final\backend\control service'; .\venv\Scripts\Activate.ps1; uvicorn main:app --host 0.0.0.0 --port 8003 }Start-Sleep -Seconds $waitTime# api gateway$job4 = Start-Job -ScriptBlock { Set-Location 'C:\Users\gabri\OneDrive\Área de Trabalho\frank\projeto final\backend\api gateway'; .\venv\Scripts\Activate.ps1; uvicorn main:app --host 0.0.0.0 --port 8000 }Start-Sleep -Seconds $waitTimeWrite-Output "Micro-services ready"